mapper.select:
SELECT 	MEMBER_ID 
FROM 	MEMBER 
WHERE 	MEMBER_ID = #{memberId}

SELECT	PASSWD,	ACTIVE_YN	
FROM	MEMBER	
WHERE	MEMBER_ID = #{memberId}

mapper.update:
UPDATE 	MEMBER
SET 	ACTIVE_YN = 'N', INACTIVE_DT = NOW()
WHERE 	MEMBER_ID = #{memberId}

mapper.select:
SELECT	COUNT(MEMBER_ID) 
FROM 	MEMBER 
WHERE 	LEFT(JOIN_AT , 10) = #{today}
